package Simulation;

import Nodes.KDC;
import Nodes.ResourceError;
import static Simulation.PubKeys.KDC;

public class KeyServer {
     //private key was generated using the class CipherRSA (in the custom Cipher package), 
     private static final String PK = "";   
     public static void main(String[] args) {
         try {
             KDC k = new KDC(KDC, 3030);
             System.out.println("Adding Private Key....");
             k.addPrivateKey(PK);
             System.out.println("Starting KDC and listening on port 3030");
             k.run();
         } catch (ResourceError ex) {
             System.out.println("Public Keys Not Added");
         }
    }
}
